// src/components/NumberOfEvents.js

import React, { useState, useEffect } from 'react';

const NumberOfEvents = ({ currentNOE, setCurrentNOE, setErrorAlert }) => {
    const [number, setNumber] = useState(currentNOE);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        setNumber(currentNOE);
    }, [currentNOE]);

    const handleInputChanged = (event) => {
        const value = event.target.value;
        setNumber(value);

        const numValue = Number(value); // Convert string to number for validation

        if (isNaN(numValue) || numValue <= 0) {
            setErrorMessage('Please enter a valid number of events');
            setErrorAlert('');
        } else if (numValue > 32) {
            setErrorMessage('Only maximum of 32 is allowed');
            setErrorAlert('');
        } else {
            setErrorMessage('');
            setErrorAlert('');
            setCurrentNOE(numValue);
        }
    };

    return (
        <div data-testid="number-of-events">
          <label>
            Number of Events:
          <input
            type="text"
            className="number"
            placeholder="Enter number of events"
            value={number}
            onChange={handleInputChanged}
            data-testid="numberOfEventsInput"
          />
          </label>
          {setErrorAlert && <span className="error-alert">{setErrorAlert}</span>}
        </div>
    );
};

export default NumberOfEvents;
