// src/App.js

import { useState, useEffect } from 'react';
import EventList from './components/EventList';
import CitySearch from './components/CitySearch';
import NumberOfEvents from './components/NumberOfEvents';
import { getEvents, extractLocations } from './api';
import './App.css';

const App = () => {
  const [events, setEvents] = useState([]);
  const [currentNOE, setCurrentNOE] = useState(32);
  const [allLocations, setAllLocations] = useState([]);
  const [currentCity, setCurrentCity] = useState('See all cities');
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchData();
  }, [currentCity, currentNOE]);

  const fetchData = async () => {
    try {
      const allEvents = await getEvents();
      const filteredEvents = currentCity === 'See all cities' ? allEvents : allEvents.filter(event => event.location === currentCity);
      setEvents(filteredEvents.slice(0, currentNOE));
      setAllLocations(extractLocations(allEvents));
    } catch (error) {
      setError('Something went wrong while fetching events');
    }
  };

  return (
    <div className="App">
      {error && <div className="error">{error}</div>}
      
      <EventList events={events} />
      <CitySearch allLocations={allLocations} setCurrentCity={setCurrentCity} />
      <NumberOfEvents currentNOE={currentNOE} setCurrentNOE={setCurrentNOE} />

      <div className="event-control">
        <label htmlFor="event-number">Number of events to display: </label>
        <input
          type="number"
          data-testid="event-number"
          value={currentNOE}
          onChange={(e) => setCurrentNOE(Number(e.target.value))}
          min="1"
          max="32"
        />
      </div>
    </div>
  );
}

export default App;
