// src/components/NumberOfEvents.js

import React, { useState, useEffect } from 'react';

const NumberOfEvents = ({ currentNOE, setCurrentNOE, setErrorAlert }) => {
    const [number, setNumber] = useState(currentNOE);
    // const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        setNumber(currentNOE);
    }, [currentNOE]);

    const handleInputChanged = (event) => {
        const value = event.target.value;
        const numValue = Number(value); // Convert string to number for validation

        setNumber(value);

        if (isNaN(numValue) || numValue <= 0) {
            setErrorAlert('Please enter a valid number of events');
        } else if (numValue > 32) {
            setErrorAlert('Only a maximum of 32 is allowed');
        } else {
            setErrorAlert('');
            setCurrentNOE(numValue);
        }
    };

    return (
        <div data-testid="number-of-events">
          <label htmlFor="numberOfEventsInput">
            Number of Events:
          <input
            type="number"
            className="number"
            placeholder="Enter number of events"
            value={number}
            onChange={handleInputChanged}
            data-testid="numberOfEventsInput"
            min={1}
            max={32}
          />
          </label>
        </div>
    );
};

export default NumberOfEvents;
