// src/components/CitySearch.js

import { useState, useEffect } from 'react';

const CitySearch = ({ allLocations }) => {
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [query, setQuery] = useState('');
  const [suggestions, setSuggestions] = useState([]);

  const handleInputChanged = (event) => {
    const value = event.target.value;
    const filteredLocations = allLocations ? allLocations.filter((location) => {
      return location.toUpperCase().indexOf(value.toUpperCase()) > -1;
    }) : [];

    console.log('Filtered Locations:', filteredLocations);

    setQuery(value);
    setSuggestions(filteredLocations);
    setShowSuggestions(value.length > 0); // to show the list
  };

  const handleItemClicked = (event) => {
    const value = event.target.textContent;
    setQuery(value);
    setShowSuggestions(false); // to hide the list
  };

  useEffect(() => {
    setSuggestions(allLocations);
  }, [`${allLocations}`]);

  return (
    <div data-testid="city-search">
      <input
        type="text"
        className="city"
        placeholder="Search for a city"
        value={query}
        onFocus={() => setShowSuggestions(true)}
        onChange={handleInputChanged}
        aria-label="City Search"
      />
      {showSuggestions ?
        <ul className="suggestions">
          {suggestions.map((suggestion) => {
            return <li onClick={handleItemClicked} key={suggestion} role="listitem">{suggestion}</li>
          })}
          <li key='See all cities' onClick={handleItemClicked} role="listitem">
            <b>See all cities</b>
          </li>
        </ul>
        : null
      }
    </div>
  )
 }

export default CitySearch;
